export { SidebarProvider, useSidebar } from "./sidebar-provider";
export { default as SidebarContainer, SideNavBarBreadcrumb } from "./sidebar-container";
export { default as SidebarContent } from "./sidebar-content";
export { default as SidebarHeader } from "./sidebar-header";
export { default as SidebarFooter } from "./sidebar-footer";
export { SidebarTrigger } from "./sidebar-trigger";
export { default as SidebarButton } from "./sidebar-button";
export { default as SidebarButtonList } from "./sidebar-button-list";
export { default as SidebarUser } from "./sidebar-user";
export { default as SideBarSeperator } from "./side-bar-seperator";

import { makePersisted } from "@solid-primitives/storage";
import {
  Accessor,
  createContext,
  createSignal,
  onCleanup,
  onMount,
  ParentProps,
  useContext,
} from "solid-js";

const [isOpen, setIsOpen] = makePersisted(createSignal(true), {
  name: "sidebar-open",
});

function useProviderValue() {
  const [isMobile, setIsMobile] = createSignal(false);

  const toggle = () => setIsOpen((prev) => !prev);
  const close = () => setIsOpen(false);

  onMount(() => {
    // Mobile detection using matchMedia
    const mediaQuery = window.matchMedia("(max-width: 767px)");
    setIsMobile(mediaQuery.matches);

    const handleMediaChange = (e: MediaQueryListEvent) => setIsMobile(e.matches);
    mediaQuery.addEventListener("change", handleMediaChange);

    // Keyboard shortcut: Cmd/Ctrl + B
    const handleKeydown = (e: KeyboardEvent) => {
      if ((e.metaKey || e.ctrlKey) && e.key === "b") {
        e.preventDefault();
        toggle();
      }
    };
    document.addEventListener("keydown", handleKeydown);

    onCleanup(() => {
      mediaQuery.removeEventListener("change", handleMediaChange);
      document.removeEventListener("keydown", handleKeydown);
    });
  });

  return {
    isOpen,
    setIsOpen,
    toggle,
    close,
    isMobile: isMobile as Accessor<boolean>,
  };
}

export type ContextType = ReturnType<typeof useProviderValue>;

const SidebarContext = createContext<ContextType | undefined>(undefined);

export const SidebarProvider = (props: ParentProps) => {
  const value = useProviderValue();
  return (
    <SidebarContext.Provider value={value}>
      {props.children}
    </SidebarContext.Provider>
  );
};

export const useSidebar = () => {
  const context = useContext(SidebarContext);
  if (!context)
    throw new Error("useSidebar must be used within a SidebarProvider");
  return context;
};

import { createSignal, JSX, onMount, Show } from "solid-js";
import { useSidebar } from "./sidebar-provider";
import { SidebarTrigger } from "./sidebar-trigger";
import { cn } from "~/lib/utils";

type SidebarContainerProps = {
  children: JSX.Element;
  appContent: JSX.Element;
  enableBreadcrumbs?: boolean;
};

const [breadcrumbs, setBreadcrumbs] = createSignal<JSX.Element>(<></>);

export default function SidebarContainer(props: SidebarContainerProps) {
  const { isOpen, isMobile, close } = useSidebar();

  const handleBackdropClick = () => {
    if (isMobile()) {
      close();
    }
  };

  return (
    <div class="flex flex-row w-full h-screen relative">
      {/* Mobile Backdrop Overlay */}
      <Show when={isMobile() && isOpen()}>
        <div
          class="fixed inset-0 z-40 bg-black/50 transition-opacity duration-300"
          onClick={handleBackdropClick}
          aria-hidden="true"
        />
      </Show>

      {/* Sidebar */}
      <div
        class={cn(
          "flex flex-col h-screen transition-all duration-300 bg-background",
          // Mobile: fixed overlay from left
          "fixed inset-y-0 left-0 z-50 md:relative md:z-auto",
          // Width and visibility
          isOpen()
            ? "w-72"
            : "-translate-x-full md:translate-x-0 md:w-0 md:overflow-hidden"
        )}
      >
        {props.children}
      </div>

      {/* Main Content Area */}
      <div class="flex-1 h-screen p-2 overflow-y-auto">
        <div class="min-h-full w-full bg-card dark:bg-card rounded-2xl shadow-md dark:shadow-none dark:border dark:border-border flex flex-col">
          <div class="border-b border-border py-3 px-3 flex items-center gap-4">
            <SidebarTrigger />
            <div class="w-full">
              <span class="text-base font-medium text-foreground" data-testid="page-title">
                {breadcrumbs()}
              </span>
            </div>
          </div>
          {props.appContent}
        </div>
      </div>
    </div>
  );
}

export function SideNavBarBreadcrumb(props: { children: JSX.Element }) {
  onMount(() => {
    setBreadcrumbs(props.children);
  });
  return null;
}

import { JSX } from "solid-js";
import { cn } from "~/lib/utils";

type SidebarContentProps = {
  children: JSX.Element;
  class?: string;
};

export default function SidebarContent(props: SidebarContentProps) {
  return (
    <div class={cn("flex-1 w-full flex flex-col py-6 min-h-0 overflow-y-auto", props.class)}>
      {props.children}
    </div>
  );
}

import { JSX } from "solid-js";

type SidebarHeaderProps = {
  children: JSX.Element;
};

export default function SidebarHeader(props: SidebarHeaderProps) {
  return <div class="flex-none w-full">{props.children}</div>;
}

import { JSX } from "solid-js";

type SidebarFooterProps = {
  children: JSX.Element;
};

export default function SidebarFooter(props: SidebarFooterProps) {
  return <div class="flex-none w-full">{props.children}</div>;
}

import { PanelLeft } from "lucide-solid";
import { Button } from "~/components/ui/button";
import { useSidebar } from "./sidebar-provider";

export function SidebarTrigger() {
  const { toggle } = useSidebar();
  return (
    <Button variant="ghost" size="icon" onClick={toggle}>
      <PanelLeft class="h-4 w-4" />
    </Button>
  );
}

import { A, useLocation } from "@solidjs/router";
import { Component, createMemo, Show } from "solid-js";
import { Button } from "~/components/ui/button";

export type SidebarButtonProps = {
  title: string;
  href: string;
  icon: Component;
};

export default function SidebarButton(props: SidebarButtonProps) {
  const location = useLocation();

  const isActiveRoute = createMemo(() => {
    return location.pathname.startsWith(props.href);
  });

  return (
    <Button
      as={A}
      href={props.href}
      variant="ghost"
      class="w-full justify-start gap-3 px-3 py-2 h-9 rounded-lg transition-all duration-300 ease-out group relative overflow-hidden border border-transparent"
      classList={{
        "bg-gradient-to-r from-primary via-primary/90 to-primary/80 text-primary-foreground shadow-lg shadow-primary/25 border-primary/20 dark:shadow-primary/10":
          isActiveRoute(),
        "hover:bg-gradient-to-r hover:from-accent/80 hover:to-accent/60 hover:shadow-md hover:shadow-accent/20 hover:border-accent/20 dark:hover:from-accent/60 dark:hover:to-accent/40":
          !isActiveRoute(),
      }}
    >
      {/* Active indicator */}
      <Show when={isActiveRoute()}>
        <div class="absolute left-0 top-1/2 -translate-y-1/2 w-1 h-6 bg-primary-foreground dark:bg-primary-foreground rounded-r-full shadow-sm" />
      </Show>

      <div class="relative z-10 flex items-center gap-2 w-full">
        <div
          class="flex items-center justify-center w-5 h-5 rounded-md transition-all duration-300"
          classList={{
            "bg-primary-foreground/20 dark:bg-primary-foreground/30":
              isActiveRoute(),
            "bg-muted/50 group-hover:bg-primary/10 dark:bg-muted/30 dark:group-hover:bg-primary/20":
              !isActiveRoute(),
          }}
        >
          <props.icon
            class="h-3.5 w-3.5 transition-all duration-300 group-hover:scale-110"
            classList={{
              "text-primary-foreground dark:text-primary-foreground":
                isActiveRoute(),
              "text-muted-foreground group-hover:text-primary dark:text-muted-foreground dark:group-hover:text-primary":
                !isActiveRoute(),
            }}
          />
        </div>
        <span
          class="text-sm font-medium tracking-wide truncate transition-all duration-300 group-hover:translate-x-0.5"
          classList={{
            "text-primary-foreground dark:text-primary-foreground":
              isActiveRoute(),
            "text-foreground group-hover:text-foreground dark:text-foreground dark:group-hover:text-foreground":
              !isActiveRoute(),
          }}
        >
          {props.title}
        </span>
      </div>

      {/* Subtle glow effect for active state */}
      <Show when={isActiveRoute()}>
        <div class="absolute inset-0 bg-gradient-to-r from-white/10 to-transparent dark:from-white/5 rounded-lg" />
      </Show>
    </Button>
  );
}

import { For } from "solid-js";
import SidebarButton from "./sidebar-button";
import type { Route } from "~/types/route";

export type SidebarButtonListProps = {
  title?: string;
  routes: Route[];
};

export default function SidebarButtonList(props: SidebarButtonListProps) {
  return (
    <nav class="space-y-3">
      <div class="px-3 py-1.5">
        <span class="text-xs font-medium text-muted-foreground dark:text-muted-foreground uppercase tracking-wider">
          {props.title || "Menu"}
        </span>
      </div>
      <div class="space-y-1 px-2">
        <For each={props.routes}>
          {(route) => (
            <SidebarButton
              title={route.title}
              href={route.href}
              icon={route.icon}
            />
          )}
        </For>
      </div>
    </nav>
  );
}

import { Separator } from "../ui/separator";

export default function SideBarSeperator() {
  return (
    <div class="px-4">
      <Separator />
    </div>
  );
}

import type { Component } from "solid-js";

export type Route = {
  title: string;
  href: string;
  icon: Component;
  roles?: string[];
};
